from time import time
import re

def solvePart1(data):
    calibration = []
    for line in data.split('\n'):
        n1, n2 = None, None
        for c in line:
            if not n1 and c.isdigit():
                n1 = c
            if c.isdigit():
                n2 = c
        calibration.append(int(n1 + n2))
    return sum(calibration)

def convertStrToDigits(s):
    valids = {'one': 'o1e', 'two': 't2e', 'three': 't3e', 'four': 'f4r', 'five': 'f5e', 'six': 's6x', 'seven': 's7n', 'eight': 'e8t', 'nine': 'n9e'}
    s_subbed = s
    for n in valids.keys():
        s_subbed = re.sub(n, valids[n], s_subbed)
        
    n1, n2 = None, None
    for c in s_subbed:
        if not n1 and c.isdigit():
            n1 = c
        if c.isdigit():
            n2 = c

    return int(n1 + n2)

def solvePart2(data):
    tot = 0
    n = 0
    res = []
    for line in data.split("\n"):
        n += 1
        x = convertStrToDigits(line)
        res.append(x)
        tot += x
    # print(res)
    return tot, n

if __name__ == '__main__':
    # read test file
    with open('test.txt', 'r') as f:
        test = f.read()
    
    # print solutions and show time elapsed
    start = time()
    
    part1 = solvePart1(test)
    p1Time = time() - start
    print(f'Part 1 ({p1Time:.4f}s):\n{part1}\n')
    
    part2 = solvePart2(test)
    p2Time = time() - p1Time - start
    print(f'Part 2 ({p2Time:.4f}s):\n{part2}\n')


